/* eslint-disable prettier/prettier */
import * as PIXI from 'pixi.js';
import PPGraph from '../../classes/GraphClass';
import PPNode, { UpdateBehaviour } from '../../classes/NodeClass';
import { CustomArgs } from '../../utils/interfaces';
import { DATATYPE, NODE_TYPE_COLOR, SOCKET_TYPE } from '../../utils/constants';
import Socket from '../../classes/SocketClass';

const defaultImage =
  'data:image/gif;base64,';

const imageInputName = 'Image';
const sizeXInputName = 'Width';
const sizeYInputName = 'Height';

const defaultWidth = 200;
const defaultHeight = 200;

export class ImageNode extends PPNode {
  sprite: PIXI.Texture;
  protected getDefaultIO(): Socket[] {
    return [
      new Socket(SOCKET_TYPE.IN, imageInputName, DATATYPE.STRING, defaultImage),
      new Socket(SOCKET_TYPE.IN, sizeXInputName, DATATYPE.NUMBER, defaultWidth),
      new Socket(
        SOCKET_TYPE.IN,
        sizeYInputName,
        DATATYPE.NUMBER,
        defaultHeight
      ),
    ];
  }

  constructor(name: string, graph: PPGraph, customArgs: CustomArgs) {
    super(name, graph, {
      ...customArgs,
      color: NODE_TYPE_COLOR.DEFAULT,
    });
    this.name = 'Draw Image';
    this.description = 'Draws an Image (base64)';

    const canvas = this.graph.viewport.getChildByName(
      'backgroundCanvas'
    ) as PIXI.Container;

    this.onExecute = async function (input) {
      canvas.removeChild(this.sprite);

      const base64 = input[imageInputName];
      if (base64) {
        const image = PIXI.Texture.from(base64);
        this.sprite = new PIXI.Sprite(image);
        this.sprite.position.set(this.x + 400, this.y);
        this.sprite.width = input[sizeXInputName];
        this.sprite.height = input[sizeYInputName];
        canvas.addChild(this.sprite);
      }
    };

    this.onNodeRemoved = (): void => {
      canvas.removeChild(this.sprite as any);
    };
    return;
  }

  protected shouldExecuteOnMove(): boolean {
    return true;
  }
}
